function m = createRadTraj(s,t)% function m = createRadTraj(s,t)%%     returns m as a radial grid with s spokes with t points%     with complex values [-0.5;0.5] (real=x-value, imag=y-value)%     s -- number of spokes%     t -- number of points within one spoke%     uses only standard matlab libraries% angle between spokesdtheta = pi/s;if (mod(t,2)==0)    %increase t to shift k-center to t/2+1    t=t+1;    %place between spoke points    dk = 1/(t-1);    %set center to zero    spv((t+1)/2)=0;    %spoke point column vector [-0.5;0.5]    for a=1:(t-1)/2        spv((t+1)/2+a)=dk*a;        spv(a)=-dk*(t-1)/2+dk*(a-1);    end    %turn spv into row vector    spv=transpose(spv);    %decrease t to retain desired number of readout points    t=t-1;else    %place between spoke points    dk = 1/(t-1);    %set center to zero    spv((t+1)/2)=0;    %spoke point column vector [-0.5;0.5]    for a=1:(t-1)/2        spv((t+1)/2+a)=dk*a;        spv(a)=-dk*(t-1)/2+dk*(a-1);    end    %turn spv into row vector    spv=transpose(spv);end%angle vector [0;pi]for a=1:s    av(a)=(a-1)*dtheta;end%turn av into row vectorav=transpose(av);%rotate by pi%spv=flipdim(spv,1);%create a complex matrix containing all the spokes%as complex numbers starting with the horizontal%line from left to right and increasing the anglefor a=1:s    for b=1:t        m(a,b)=spv(b)*exp(1i*av(a));    endend